import { Button } from "@mui/material";
import React, { useState } from "react";
import "./Signup.css";

function Signup() {
  const [user, setUser] = useState({
    name: "",
    email: "",
    phone: "",
    occupation: "",
    password: "",
    cpassword: "",
  });
  let name, value;
  const signupInput = (e) => {
    console.log(e);
    name: e.target.name;
    value: e.target.value;

    setUser({ ...user, [name]: value });
  };

  return (
    <>
      <form className="Signup">
        <h1>Sign-up</h1>
        <input
          value={user.name}
          onChange={signupInput}
          name="name"
          type="text"
          placeholder="name"
        />
        <input
          value={user.email}
          onChange={signupInput}
          name="email"
          type="email"
          placeholder="email"
        />
        <input
          value={user.phone}
          onChange={signupInput}
          name="phone"
          type="text"
          placeholder="phone"
        />
        <input
          value={user.occupation}
          onChange={signupInput}
          name="occupation"
          type="text"
          placeholder="occupation"
        />
        <input
          value={user.password}
          onChange={signupInput}
          name="password"
          type="password"
          placeholder="password"
        />
        <input
          value={user.cpassword}
          onChange={signupInput}
          name="cpassword"
          type="password"
          placeholder="confirm-password"
        />
        <Button variant="contained">Contained</Button>
      </form>
    </>
  );
}

export default Signup;
